<main role="main">   <section id="login_page" class="bg_img">      <div class="bg_b2">         <div class="container">            <div class="row justify-content-center">               <div class="col-md-8">                  <div class="alert alert-success" id="contact-id" style="display:none;">Your information has been sent successfully.Admin can contact shortly as soon as possible .</div>                      <form id="contactForm"  action="#" method="post" role="form" novalidate="">                                     <div class="card">                        <h2 class="text-center text-white mt-3">Send us a message. We'd be glad to help.</h2>                        <div class="card-body">                           <div class="form-row">                              <div class="form-group col-md-6">                                 <label>Full Name</label>                                 <input type="text" class="form-control" name="contactName" required />                              </div>                              <div class="form-group col-md-6">                                 <label>Your Email</label>                                 <input type="email" class="form-control" name="contactEmail"  required />                              </div>                           </div>                           <div class="form-row">                              <div class="form-group col-md-12">                                 <label>Message</label>                                 <textarea class="form-control" name="contactMessage" rows="5" required></textarea>                              </div>                           </div>                           <div class="mt-2 mx-auto">                              <input type="submit" class="btn btn-success" value="Send Message">                           </div>                        </div>                     </div>                  </form>               </div>            </div>         </div>      </div>   </section></main>